import React, { useState } from 'react';
import { Link } from "react-router-dom";

import './Join.css';

export default function SignIn() {
  const [name, setName] = useState('');
  const [room, setRoom] = useState('');

  return (
    <div className="container mx-auto py-10">
      <div className="max-w-lg mx-auto">
        <h1 className="">Вход</h1>
        <div>
          <input placeholder="Ваше имя" className="" type="text" onChange={(event) => setName(event.target.value)} />
          <input placeholder="Номер комнаты" className="" type="text" onChange={(event) => setRoom(event.target.value)} />
        </div>
        <Link onClick={e => (!name || !room) ? e.preventDefault() : null} to={`/chat?name=${name}&room=${room}`}>
          <button className={'button mt-20'} type="submit">Присоединиться</button>
        </Link>
      </div>
    </div>
  );
}
